Clear
function Hide-Console
{
    Add-Type -Name Window -Namespace Console -MemberDefinition '
    [DllImport("Kernel32.dll")]
    public static extern IntPtr GetConsoleWindow();

    [DllImport("user32.dll")]
    public static extern bool ShowWindow(IntPtr hWnd, Int32 nCmdShow);
    '
    $consolePtr = [Console.Window]::GetConsoleWindow()
    [Console.Window]::ShowWindow($consolePtr, 0)
}

Hide-Console

Add-Type -assembly System.Windows.Forms
Add-Type -assembly System.Drawing
add-type -assembly Microsoft.VisualBasic

$MsgBet0To100 = "Select a number Between 0 and 1000!!!"
$DontWanaPlay = "Ok you don''t wanna Play, bye!','No more Game"

function Show-PlayAgain
{
    $GuessBox.Text = ""
    $GuessBox.Enabled = $false
    $PlayAgain.Visible = $true
    $PlayAgain.BringToFront()
    $button.Enabled = $false
    $Form1.Refresh()
}

Function PlayBet
{
    Clear

    $GLOBAL:ToFind = $null
    $GLOBAL:MaxTrials = 10
    $GLOBAL:Trials = 0

    $betInput =  [System.Windows.Forms.MessageBox]::Show("Wanna Play Alone ? `r`n`r`nYes == I''ll Choose a number for you`r`n No == Your friend Will Choose a Number for you","Play Alone?","YesNoCancel","Info")
    switch  ($betInput) {
        'Yes' {
            $ToFind = Get-Random -Maximum 1000
        }
        'No' {
            while ($ToFind -gt 1000 -or $ToFind.Length -eq 0)
            {
                try
                {
                    $ToFind = [convert]::ToInt32([Microsoft.VisualBasic.Interaction]::InputBox("Set the Price to find", "Set the Price to find"))

                    if ($ToFind -gt 1000)
                    {
                        [System.Windows.Forms.MessageBox]::Show($MsgBet0To100,"One more time","Ok","Error")
                    }
                }
                catch
                {
                    [System.Windows.Forms.MessageBox]::Show($MsgBet0To100,"One more time","Ok","Error")
                }
            }
        }
        'Cancel' {
            [System.Windows.Forms.MessageBox]::Show($DontWanaPlay,"Exit","Ok","Info")
            return
        }
    }

    $GuessBox_changed=
    {
        if($GuessBox.Text.Length -gt 0)
        {
            if ([convert]::ToInt32($GuessBox.Text) -gt 1000)
            {
                $LabelWin.Text = $MsgBet0To100
                $Button.Enabled = $false
                return
            }
            else
            {
                $LabelWin.Text = ""
            }
            $Button.Enabled = $true
        }
        else
        {
            $Button.Enabled = $false
        }
    }

    $PlayAgain_OnClick=
    {
        $Form1.Dispose()
        $Form1.Close()
        PlayBet
    }

    $Button_OnClick=
    {
        if($GuessBox.Text.Length -eq 0)
        {
            $LabelWin.Text = "I bet you forgot to give a number `r`n $Msg"
        }
        else
        {
            $GLOBAL:Trials++
            if ($Trials -le $MaxTrials)
            {
                $TrialsLeft = $MaxTrials - $Trials
                $Msg = "You have $TrialsLeft trial(s) Left "

                if($ToFind -gt [convert]::ToInt32($GuessBox.Text))
                {
                    $LabelWin.Text = "Price is Greater `r`n $Msg"
                }
                elseif($ToFind -lt [convert]::ToInt32($GuessBox.Text))
                {
                    $LabelWin.Text = "Price is lesser `r`n $Msg"
                }
                elseif($ToFind -eq [convert]::ToInt32($GuessBox.Text))
                {
                    Show-PlayAgain
                    $LabelWin.ForeColor = "Green"
                    $LabelWin.Text = "This is Exact, Price is $ToFind"
                    return
                }

                if ($Trials -ge $MaxTrials)
                {
                    Show-PlayAgain
                    $LabelWin.Text = "You tried to many times `r`n Correct Price was $ToFind"
                    return
                }    
            }
        }
    }

    $Form1 = New-Object System.Windows.Forms.Form
    $Form1.Text ="The Just Price Game, SYNAPSYS Pow@"
    $Form1.Width = 440
    $Form1.Height = 200
    $Form1.FormBorderStyle = "FixedDialog"
    $Form1.MaximizeBox = $false
    $Form1.StartPosition = "CenterScreen"

    $base64IconString = ""
    $iconimageBytes = [Convert]::FromBase64String($base64IconString)
    $synIcon = New-Object IO.MemoryStream($iconimageBytes, 0, $iconimageBytes.Length)
    $synIcon.Write($iconimageBytes, 0, $iconimageBytes.Length);
    $Form1.Icon = [System.Drawing.Icon]::FromHandle((new-object System.Drawing.Bitmap -argument $synIcon).GetHIcon())

    $GuessBox = New-Object System.Windows.Forms.Textbox
    $GuessBox.Location  = New-Object System.Drawing.Point(180,10)
    $GuessBox.Width = 80
    $GuessBox.MaxLength  = 4

    $Button = New-Object System.Windows.Forms.Button
    $Button.Location  = New-Object System.Drawing.Point(270,8)
    $Button.text = "Bet"
    $Button.Enabled = $false

    $Label = New-Object System.Windows.Forms.Label
    $Label.Text = "Bet a Price Between 0 and 1000:"
    $Label.Location  = New-Object System.Drawing.Point(10,13)
    $Label.AutoSize = $true

    $PlayAgain = New-Object System.Windows.Forms.Button
    $PlayAgain.Location  = New-Object System.Drawing.Point(180,135)
    $PlayAgain.text = "Play Again"
    $PlayAgain.Visible = $false
    $PlayAgain.AutoSize = $true
    
    $LabelWin = New-Object System.Windows.Forms.Label
    $LabelWin.Location  = New-Object System.Drawing.Point(0,60)
    $LabelWin.Text = ""
    $LabelWin.TextAlign = "TopCenter"
    $LabelWin.Height = "90"
    $LabelWin.Width = "440"
    $LabelWin.ForeColor = "Red"
    $LabelWin.Font = [System.Drawing.Font]::new('Microsoft Sans Serif', 20, [System.Drawing.FontStyle]::Regular)

    $Form1.Controls.Add($LabelWin)
    $Form1.Controls.Add($Label)
    $Form1.Controls.Add($GuessBox)
    $Form1.Controls.Add($Button)
    $Form1.Controls.Add($PlayAgain)
    
    $Button.add_Click($Button_OnClick)
    $PlayAgain.add_Click($PlayAgain_OnClick)
    
    $GuessBox.Add_TextChanged({$this.Text = $this.Text -replace '\D'})
    $GuessBox.Add_TextChanged($GuessBox_changed)
    
    $Form1.ShowDialog()
}

PlayBet

